import { useState } from 'react';
import Expenses from '../src/components/Expenses/Expenses';
import NewExpenses from '../src/components/NewExpenses/NewExpense';

const dummy = [
    {
      id: 'e1',
      title: 'Toilet Paper',
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: 'e3',
      title: 'Car Insurance',
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      date: new Date(2021, 5, 12),
    },
];

function App() {
  const [expensesArray, setExpensesArray] = useState(dummy);
  function getNewExpenseDataHandler(newExpenseData) {
    setExpensesArray(function (prev) {
      return [
        newExpenseData,
        ...prev
      ]
    })
  }
  return (
      <div>
        <NewExpenses getNewExpenseData={getNewExpenseDataHandler}/>
        <Expenses expenses={expensesArray} />
      </div>
  )
}

export default App;
