import { useState } from 'react';
import './Expenses.css';
import './ExpenseDate.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';

function Expenses(props) {
  const [expenseItems, setExpenseItems] = useState(props.expenses);
  const [filteredArray, setFilteredArray] = useState(expenseItems);
  const [arrayToBeDisplayed, setArrayToBeDisplayed] = useState(expenseItems);
  var filterApply = false;

  function filterYear(year) {
    filterApply = true;
    if(year !== '0000') {
      setFilteredArray(expenseItems.filter(function (expense) {
        return expense.date.getFullYear().toString() === year;
      }))
    }
  }
  
  {filterApply ? filteredArray : expenseItems}
  return (
    <Card className="expenses">
      <ExpenseFilter filterYearChange={filterYear}/>
      {expenseItems.map(function(expense, index) {
        return (
          <ExpenseItem 
            key = {index}
            date = {expense.date}
            title = {expense.title}
            amount = {expense.amount}
          />
        )
      })}
      {filteredArray.map(function(expense, index) {
        return (
          <ExpenseItem 
            key = {index}
            date = {expense.date}
            title = {expense.title}
            amount = {expense.amount}
          />
        )
      })}
    </Card>
  )
}

export default Expenses;
